import './App.css';
import Button from './components/Button';

const App = () => {
  const buttonTexts = ['0','1','2','3']
  const [monitorText, setMonitorText] = useState('');
  const [numbers, setNumber1] = useState([0,0]);
  const [currentIndex,setCurrentIndex] = useState(0);
  const [number2, setNumber2] = useState(0);
  const [operator, setOperator] = useState('');
  const [decimalCount, setDecimalCount] = useState(0);
  const [enterCount, setEnterCount] = useState(0);
  const [currentNumber, setCurrentNumber] = useState(0);

const renderButtons = buttonTexts.map((txt,index) => {
  return(<Button key = {index} text={txt} />);

});

  return(
    <>
      <div className = "ui inverted segment">
         {renderButtons}
      </div>
    </>
  );
}

export default App;
