import React , { useState }from 'react';
import './App.css';
import Button from './components/Button';
import Monitor from './components/Monitor';

const App = () => {
  const [monitorText, setMonitorText] = useState('');
  const [number1, setNumber1] = useState(0);
  const [number2, setNumber2] = useState(0);
  const [currentIndex,setCurrentIndex] = useState(0);
  const [operator, setOperator] = useState('');
  const [decimalCount, setDecimalCount] = useState(0);
  const [enterCount, setEnterCount] = useState(0);
  const [currentNumber, setCurrentNumber] = useState('0');

const onClickTask = (type,value) => {
  switch(type){
    case 'number': fnCurrentNumber(value);break;
    case 'decimal': fnDecimal();break;
  }

}
const fnCurrentNumber = value =>{
  if(currentNumber !== '0'){
    let newNumber = currentNumber + value.toString();
    setCurrentNumber(newNumber);
    if(enterCount !== 0){setEnterCount(0)}
  }
  else{setCurrentNumber(value);}
}
const buttonTexts = ['6','7','8','9','5','4','3','2','1','0','.','AC','+','-','*','/','Enter'];
const renderButtons = buttonTexts.map(txt => <Button key = {txt} value={txt} onClickTask={onClickTask}/>);



  return(
    <>

      <div className = "calculator">
        <Monitor display = {currentNumber}/>
        <div className = "ui inverted segment">
         {renderButtons}
         </div>
      </div>
    </>
  );
}

export default App;
