import React , { useState }from 'react';
import './App.css';
import Button from './components/Button';

const App = () => {
  const buttonTexts = ['6','7','8','9','5','4','3','2','1','0','.','AC','+','-','*','/'];

const renderButtons = buttonTexts.map(txt => <Button index = {txt} text={txt} />);

  return(
    <>
      <div className = "ui inverted segment calculator">
         {renderButtons}
      </div>
    </>
  );
}

export default App;
